    static protected function pickRPC() :string {
        $rpcs = [];

        $n = strtolower(getenv("ETHEREUM_NETWORK"));
        if (getenv("ETHEREUM_NETWORK") == "mainnet") {
            $rpcs = config('app.ethereum_rpc.mainnet');
        } else {
            $rpcs = config('app.ethereum_rpc.kovan');
        }

        return $rpcs[rand() % count($rpcs)];
    }

    static public function getOpenSky() {
        $contract = new SmartContract(
            json_decode(self::ABI_OPENSKY),
            getenv("CLOSESKY_CONTRACT_ADDRESS"),
            new Ethereum(self::pickRPC()),
        );

        return $contract;
    }

    static public function getErc721($contract_address) {
        $contract = new SmartContract(
            json_decode(self::ABI_ERC721),
            $contract_address,
            new Ethereum(self::pickRPC()),
        );

        return $contract;
    }

